args:
$p = "c:\Users\bg005589\OneDrive - Université Côte d'Azur\Bureau\machinlearning\nlp2";
Set-Location $p;
# Moves
Move-Item -Path .\comments_labeled_binary.csv -Destination .\data\processed\ -Force -ErrorAction SilentlyContinue;
Move-Item -Path .\tp_min_models.py -Destination .\src\train\ -Force -ErrorAction SilentlyContinue;
# viz_clean.py may not exist at root; only move if present
if (Test-Path .\viz_clean.py) { Move-Item -Path .\viz_clean.py -Destination .\src\viz\ -Force };
Move-Item -Path .\tp_min_results.csv -Destination .\reports\ -Force -ErrorAction SilentlyContinue;
Move-Item -Path .\tp_test_predictions_tree.csv -Destination .\reports\ -Force -ErrorAction SilentlyContinue;

# Archive list
$toArchive = @(
 'comments_labeled_binary_backup.csv',
 'comments_labeled_binary_hf.csv',
 'comments_only.csv',
 'comments_labeled.csv',
 'tp_min_models_backup.py',
 'tp1.py',
 'comv.py',
 'csvnet.py',
 'label.py',
 'projet.py',
 'projet2.py',
 'rt-polarity-neg.txt',
 'rt-polarity-pos.txt',
 'VKancdDIpOU.info.json'
)
$archiveDir = Join-Path $p 'archive\2026-01-08'
$index = @()
foreach ($f in $toArchive) {
    if (Test-Path $f) {
        Move-Item -Path $f -Destination $archiveDir -Force;
        $index += ("$f\t Moved to archive\\2026-01-08\")
    } else {
        $index += ("$f\t NOT FOUND")
    }
}
# Write INDEX
$index | Out-File -FilePath (Join-Path $archiveDir 'INDEX.txt') -Encoding utf8
Write-Host 'Dry-run completed: files moved (if present), archive INDEX created.';
